class PortfolioPage extends StatelessWidget {
  const PortfolioPage({Key? key}) : super(key: key);

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: const Text('My Portfolio'),
      ),
      body: LayoutBuilder(
        builder: (BuildContext context, BoxConstraints constraints) {
          // Determine if the layout should be vertical based on the available width
          bool isVertical = constraints.maxWidth < 720;
          int crossCount = 1;
          if (!isVertical) {
            crossCount = 3;
            if (constraints.maxWidth < 1080) {
              crossCount = 2;
            }
          }
          return GridView.builder(
            padding: const EdgeInsets.all(16),
            itemCount: gameCards.length,
            gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
              crossAxisCount: crossCount,
              childAspectRatio: isVertical ? 1.5 : 2 / 1,
              crossAxisSpacing: 16,
              mainAxisSpacing: 16,
            ),
            itemBuilder: (BuildContext context, int index) {
              return GameCard(
                imageUrl: gameCards[index].imageUrl,
                link: gameCards[index].link,
                gameTitle: gameCards[index].gameTitle,
                role: gameCards[index].role,
                platformTarget: gameCards[index].platformTarget,
                isVertical: isVertical,
              );
            },
          );
        },
      ),
    );
  }

}
